mapper.update:
update mt_order
set status = #{toStatus},
    update_time = NOW()
where id = #{id}
and status = #{fromStatus}

mapper.select:
select *
from mt_order
where user_id = #{userId}

    and status = #{status}

order by update_time desc
limit #{offset}, #{limit}

select count(*)
from mt_order
where user_id = #{userId}

    and status = #{status}
